import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import styles from '../styles/RemoveComponent.module.css';

export default function RemoveComponent() {
  const [serialNumbers, setSerialNumbers] = useState('');
  const [reason, setReason] = useState('');
  const [isBatch, setIsBatch] = useState(false);
  const [feedback, setFeedback] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    // Add logic to handle form submission and deletion
    console.log({ serialNumbers, reason, isBatch });
    setFeedback('Components processed for removal.');
  };

  return (
    <div className={styles.container}>
      <header className={styles.header}>
        <Link to="/home" className={styles.backLink}>
          ← Back to Home
        </Link>
        <h1 className={styles.title}>Remove Component</h1>
      </header>

      <div className={styles.content}>
        <form onSubmit={handleSubmit} className={styles.form}>
          <div className={styles.field}>
            <label htmlFor="serialNumbers">Serial Numbers (comma separated):</label>
            <textarea
              id="serialNumbers"
              value={serialNumbers}
              onChange={(e) => setSerialNumbers(e.target.value)}
              rows="4"
              required
            />
          </div>

          <div className={styles.field}>
            <label htmlFor="reason">Reason for Removal:</label>
            <input
              id="reason"
              type="text"
              value={reason}
              onChange={(e) => setReason(e.target.value)}
              required
            />
          </div>

          <div className={styles.field}>
            <label>
              <input
                type="checkbox"
                checked={isBatch}
                onChange={() => setIsBatch(!isBatch)}
              />
              Remove as Batch
            </label>
          </div>

          <button type="submit" className={styles.submitBtn}>Submit</button>
        </form>

        {feedback && <p className={styles.feedback}>{feedback}</p>}
      </div>

      <footer className={styles.footer}>
        <p>© 2024 Your Company</p>
      </footer>
    </div>
  );
}
