import React, { useState } from 'react';
//mport { useNavigate } from 'react-router-dom';
import avatar from '../assests/profile.webp';
import styles from '../styles/Username.module.css';
import { Link, useNavigate } from 'react-router-dom';
import { Toaster } from 'react-hot-toast';
import { useFormik } from 'formik';
import { usernameValidate } from '../helper/validate';

export default function Username() {
  const [username, setUsername] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const formik = useFormik({
    initialValues : {
      username : ''
    },
    validate : usernameValidate,
    validateOnBlur: false,
    validateOnChange: false,
    onSubmit : async values => {
      console.log(values)
    }
  })

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!username) {
      setError('Username is required');
    } else {
      setError('');
      // Store the username if necessary and navigate to the password page
      navigate('/password'); // Navigate to the Password page
    }
  };

  return (
    <div className="container mx-auto">
      <Toaster position='top-center' reverseOrder={false}></Toaster>
      <div className='flex justify-center items-center h-screen'>
        <div className={styles.glass}>
          <div className="title flex flex-col items-center">
            <h4 className='text-5xl font-bold'>Login Page</h4>
            <span className='py-4 text-xl w-2/3 text-center text-gray-500'>
              Enter credentials
            </span>
          </div>
          <form className='py-1' onSubmit={formik.handleSubmit}>
            <div className='profile flex justify-center py-4'>
              <img src={avatar} className={styles.profile_img} alt="avatar" />
            </div>

            <div className="textbox flex flex-col items-center gap-6">
              <input {...formik.getFieldProps('username')}
                className={styles.textbox} 
                type="text" 
                placeholder='Username' 
                value={username} 
                onChange={(e) => setUsername(e.target.value)} 
              />
              {error && <p className="text-red-500">{error}</p>}
              <button className={styles.btn} type='submit'>Next</button>
            </div>

            <div className="text-center py-4">
              <span className='text-gray-500'>Not a Member? <Link className='text-red-500' to="/register">Register</Link></span>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

